// Import additional tools
const { additionalTools } = require('./additional-tools.js');
const { moreTools } = require('./more-tools.js');
const { finalTools } = require('./final-tools.js');
const { extraTools } = require('./extra-tools.js');
const { bonusTools } = require('./bonus-tools.js');
const { ultimateTools } = require('./ultimate-tools.js');
const { finalPushTools } = require('./final-push-tools.js');

// Merge all tools
const allTools = [...tools, ...additionalTools, ...moreTools, ...finalTools, ...extraTools, ...bonusTools, ...ultimateTools, ...finalPushTools];

// Generate the final database
function generateDatabase() {
    const database = {
        version: DATABASE_VERSION,
        lastUpdated: LAST_UPDATED,
        schema: {
            version: "2.0",
            features: ["semantic-search", "dependency-mapping", "security-posture", "versioning", "multilingual"]
        },
        stats: {
            totalTools: allTools.length,
            totalCategories: categories.length,
            fossTools: allTools.filter(t => t.is_foss === true).length,
            lastGenerated: new Date().toISOString()
        },
        categories: categories,
        categoryGroups: categoryGroups,
        tools: allTools.map(tool => ({
            ...tool,
            license_type: tool.license,
            searchTerms: generateSearchTerms(tool)
        }))
    };

    return database;
}

// Generate search terms for better indexing
function generateSearchTerms(tool) {
    const terms = [
        tool.name.toLowerCase(),
        tool.tagline.toLowerCase(),
        ...(tool.tags || []),
        ...(tool.alternatives_to || []).map(a => a.toLowerCase()),
        tool.category
    ];
    return [...new Set(terms)].join(' ');
}

// Write the database to file
function writeDatabase() {
    const database = generateDatabase();
    const outputPath = path.join(__dirname, '..', 'assets', 'data', 'tools.json');
    
    fs.writeFileSync(outputPath, JSON.stringify(database, null, 2));
    
    console.log(` Database generated successfully!`);
    console.log(`   Total tools: ${database.stats.totalTools}`);
    console.log(`   Total categories: ${database.stats.totalCategories}`);
    console.log(`   FOSS tools: ${database.stats.fossTools}`);
    console.log(`   Output: ${outputPath}`);
}

// Run the generator
writeDatabase();
